const NavLink = ({ children }: { children: React.ReactNode }) => (
  <a href="#" className="px-4 py-2 text-foreground hover:bg-accent rounded-lg transition-colors">
    {children}
  </a>
);

export default Navbar;

interface ProductBadgeProps {
  title: string;
  rank: string;
}

const ProductBadge = ({ title, rank }: ProductBadgeProps) => {
  return (
    <div className="flex items-center gap-1">
      {/* Left Laurel */}
      <svg width="32" height="48" viewBox="0 0 32 48" className="text-badge-laurel">
        <g fill="currentColor">
          <ellipse cx="8" cy="8" rx="4" ry="7" transform="rotate(-30 8 8)" />
          <ellipse cx="6" cy="18" rx="4" ry="7" transform="rotate(-20 6 18)" />
          <ellipse cx="5" cy="28" rx="4" ry="7" transform="rotate(-10 5 28)" />
          <ellipse cx="6" cy="38" rx="4" ry="7" transform="rotate(5 6 38)" />
        </g>
      </svg>

      <div className="text-center px-2">
        <p className="text-badge-orange text-sm font-medium tracking-wide">{title}</p>
        <p className="text-badge-orange text-2xl font-bold">{rank}</p>
      </div>

      {/* Right Laurel */}
      <svg width="32" height="48" viewBox="0 0 32 48" className="text-badge-laurel transform scale-x-[-1]">
        <g fill="currentColor">
          <ellipse cx="8" cy="8" rx="4" ry="7" transform="rotate(-30 8 8)" />
          <ellipse cx="6" cy="18" rx="4" ry="7" transform="rotate(-20 6 18)" />
          <ellipse cx="5" cy="28" rx="4" ry="7" transform="rotate(-10 5 28)" />
          <ellipse cx="6" cy="38" rx="4" ry="7" transform="rotate(5 6 38)" />
        </g>
      </svg>
    </div>
  );
};

export default ProductBadge;

import { Link, Clock, Lock, Tag } from 'lucide-react';

interface FeatureCardProps {
  title: string;
  time: string;
  detail: string;
  activeDays: number[];
  color: 'blue' | 'purple' | 'pink' | 'green';
  icon: 'link' | 'clock' | 'lock' | 'tag';
}

const colorClasses = {
  blue: 'bg-card-blue',
  purple: 'bg-card-purple',
  pink: 'bg-card-pink',
  green: 'bg-card-green',
};

const iconMap = {
  link: Link,
  clock: Clock,
  lock: Lock,
  tag: Tag,
};

const days = ['一', '二', '三', '四', '五', '六', '日'];

const FeatureCard = ({ title, time, detail, activeDays, color, icon }: FeatureCardProps) => {
  const IconComponent = iconMap[icon];

  return (
    <div className={`${colorClasses[color]} rounded-3xl p-5 min-w-[280px] w-[280px] flex-shrink-0 relative overflow-hidden`}>
      {/* Emoji avatar */}
      <div className="absolute top-4 right-4 w-14 h-14 bg-card rounded-2xl flex items-center justify-center text-3xl shadow-sm">
        👩
      </div>

      {/* Icon and Title */}
      <div className="flex items-center gap-2 mb-8">
        <IconComponent className="w-5 h-5 text-foreground/70" />
        <h3 className="font-semibold text-lg text-foreground">{title}</h3>
      </div>

      {/* Time and Detail */}
      <div className="space-y-1 mb-6">
        <div className="flex items-center gap-2 text-foreground/70">
          <Clock className="w-4 h-4" />
          <span className="text-sm">{time}</span>
        </div>
        <div className="flex items-center gap-2 text-foreground/70">
          <Lock className="w-4 h-4" />
          <span className="text-sm">{detail}</span>
        </div>
      </div>

      {/* Days selector */}
      <div className="flex gap-1.5">
        {days.map((day, index) => (
          <button
            key={day}
            className={`w-9 h-9 rounded-lg text-sm font-medium transition-colors ${
              activeDays.includes(index)
                ? 'bg-card text-foreground shadow-sm'
                : 'text-foreground/40'
            }`}
          >
            {day}
          </button>
        ))}
      </div>
    </div>
  );
};

export default FeatureCard;

import { Apple, Shuffle } from 'lucide-react';
import ProductBadge from './ProductBadge';

const HeroSection = () => {
  return (
    <section className="pt-32 pb-16 px-4 text-center">
      {/* Product Badges */}
      <div className="flex justify-center items-center gap-8 mb-10">
        <ProductBadge title="Product of the day" rank="1st" />
        <ProductBadge title="Product of the week" rank="1st" />
      </div>

      {/* Main Heading */}
      <h1 className="text-5xl md:text-6xl font-black text-foreground mb-6 tracking-tight">
        妈妈说，时间到了
      </h1>

      {/* Subheading */}
      <p className="text-lg text-muted-foreground max-w-xl mx-auto mb-10 leading-relaxed">
        妈妈闹钟让你按计划行事——屏蔽干扰，并督促你完成你承诺过的事。
      </p>

      {/* CTA Buttons */}
      <div className="flex justify-center items-center gap-3">
        <button className="flex items-center gap-2 px-6 py-3.5 bg-primary text-primary-foreground rounded-xl font-medium hover:opacity-90 transition-all shadow-button">
          <Apple className="w-5 h-5" />
          下载 App
        </button>
        <button className="flex items-center gap-2 px-6 py-3.5 bg-card text-foreground border border-border rounded-xl font-medium hover:bg-accent transition-colors shadow-card">
          <Shuffle className="w-5 h-5" />
          换一换
        </button>
      </div>
    </section>
  );
};

export default HeroSection;

