@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@400;500;600;700;900&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    /* Background - Warm cream/beige */
    --background: 40 33% 96%;
    --foreground: 30 15% 15%;

    /* Primary - Orange */
    --primary: 16 76% 60%;
    --primary-foreground: 0 0% 100%;

    /* Secondary */
    --secondary: 30 20% 92%;
    --secondary-foreground: 30 15% 25%;

    /* Muted */
    --muted: 40 20% 92%;
    --muted-foreground: 30 10% 45%;

    /* Card */
    --card: 0 0% 100%;
    --card-foreground: 30 15% 15%;

    /* Popover */
    --popover: 0 0% 100%;
    --popover-foreground: 30 15% 15%;

    /* Border */
    --border: 30 15% 88%;
    --input: 30 15% 88%;
    --ring: 16 76% 60%;

    /* Accent */
    --accent: 40 20% 94%;
    --accent-foreground: 30 15% 15%;

    /* Destructive */
    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 100%;

    /* Feature Card Colors */
    --card-blue: 218 77% 88%;
    --card-purple: 258 55% 87%;
    --card-pink: 350 65% 91%;
    --card-green: 145 55% 87%;

    /* Badge */
    --badge-orange: 16 76% 55%;
    --laurel-green: 80 35% 42%;

    /* Radius */
    --radius: 0.75rem;
  }

  .dark {
    --background: 30 10% 10%;
    --foreground: 40 20% 95%;
    --primary: 16 76% 60%;
    --primary-foreground: 0 0% 100%;
    --card: 30 10% 15%;
    --card-foreground: 40 20% 95%;
    --popover: 30 10% 15%;
    --popover-foreground: 40 20% 95%;
    --muted: 30 10% 20%;
    --muted-foreground: 40 20% 70%;
    --border: 30 10% 25%;
    --secondary: 30 10% 20%;
    --secondary-foreground: 40 20% 90%;
    --accent: 30 10% 20%;
    --accent-foreground: 40 20% 95%;
  }
}

* {
  border-color: hsl(var(--border));
}

body {
  background-color: hsl(var(--background));
  color: hsl(var(--foreground));
  font-family: 'Noto Sans SC', system-ui, -apple-system, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* Custom scrollbar */
::-webkit-scrollbar {
  height: 6px;
}

::-webkit-scrollbar-track {
  background: transparent;
}

::-webkit-scrollbar-thumb {
  background: hsl(var(--border));
  border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
  background: hsl(var(--muted-foreground));
}

