import { Link, Clock } from "lucide-react";

interface TaskCardProps {
  title: string;
  time: string;
  apps: string;
  days: string[];
  activeDays: string[];
  color: "blue" | "purple" | "pink" | "green";
}

const colorMap = {
  blue: "bg-card-blue",
  purple: "bg-card-purple",
  pink: "bg-card-pink",
  green: "bg-card-green",
};

const TaskCard = ({ title, time, apps, days, activeDays, color }: TaskCardProps) => {
  return (
    <div className={`${colorMap[color]} rounded-3xl p-5 min-w-[300px] h-[220px] relative flex-shrink-0`}>
      {/* Header */}
      <div className="flex items-start justify-between">
        <div className="flex items-center gap-2">
          <Link className="w-5 h-5 text-foreground/70" />
          <h3 className="text-lg font-semibold text-foreground">{title}</h3>
        </div>
        <span className="text-3xl">ğŸ‘©</span>
      </div>

      {/* Time & Apps */}
      <div className="mt-4 space-y-1">
        <div className="flex items-center gap-2 text-foreground/70">
          <Clock className="w-4 h-4" />
          <span className="text-sm">{time}</span>
        </div>
        <div className="flex items-center gap-2 text-foreground/70">
          <svg className="w-4 h-4" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <rect x="3" y="11" width="18" height="11" rx="2" ry="2"/>
            <path d="M7 11V7a5 5 0 0 1 10 0v4"/>
          </svg>
          <span className="text-sm">{apps}</span>
        </div>
      </div>

      {/* Days */}
      <div className="absolute bottom-5 left-5 right-5">
        <div className="flex gap-1">
          {days.map((day) => (
            <div
              key={day}
              className={`w-10 h-10 rounded-full flex items-center justify-center text-sm font-medium transition-all
                ${activeDays.includes(day)
                  ? "bg-card text-foreground shadow-sm"
                  : "text-foreground/40"
                }`}
            >
              {day}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TaskCard;

