        </div>
        <div className="flex items-center gap-2 text-foreground/70">
          <Lock className="w-4 h-4" />
          <span className="text-sm">{detail}</span>
        </div>
      </div>

      {/* Days selector */}
      <div className="flex gap-1.5">
        {days.map((day, index) => (
