import { Link, Clock, Lock, Tag } from 'lucide-react';

interface FeatureCardProps {
  title: string;
  time: string;
  detail: string;
  activeDays: number[];
  color: 'blue' | 'purple' | 'pink' | 'green';
  icon: 'link' | 'clock' | 'lock' | 'tag';
}

const colorClasses = {
  blue: 'bg-card-blue',
  purple: 'bg-card-purple',
  pink: 'bg-card-pink',
  green: 'bg-card-green',
};

const iconMap = {
  link: Link,
  clock: Clock,
  lock: Lock,
  tag: Tag,
};

const days = ['ä¸€', 'äºŒ', 'ä¸‰', 'å››', 'äº”', 'å…­', 'æ—¥'];

